<div id="EUWarning" class="miniModal">
    <div>
        <span onclick="EU_CLOSE('EUWarning');" class="closing">&times;</span>
        <img src="data:image/png;base64,"/>
        <p>This website is designed for and targeted to U.S. audiences and is governed by and operated in accordance with U.S. laws. By continuing to use this website you consent to the terms of our <a href="/privacy-statement">privacy policy</a>.</p>
    </div>
</div>
<div id="EU" class="miniModal">
    <div>
        <span onclick="EU_CLOSE('EU');" class="closing">&times;</span>
        <img src="data:image/png;base64,"/>
        <p>This website is not currently accepting inquiries from visitors in EU countries.</p>
    </div>
</div>
<script>
    document.body.classList.add("eu");
    window.addEventListener('load', function() {
        if(!sessionStorage.EUFirstVisit) {
            sessionStorage.EUFirstVisit = 'No';
            document.getElementById('EUWarning').style.display = 'block';
        }
        var basketPath = document.getElementById('cart');
        if (basketPath !== null) {
            basketPath.onclick = function(event){
                event.preventDefault();
                document.getElementById('EU').style.display = 'block';
            };
        }
        var cart_bottom = document.getElementById('cart_bottom');
        if (cart_bottom !== null) {
            cart_bottom.onclick = function(event){
                event.preventDefault();
                document.getElementById('EU').style.display = 'block';
            };
        }
    });
    //CLOSE EU MODAL
    function EU_CLOSE(modal){
        document.body.classList.remove("eu");
        document.getElementById(modal).style.display = 'none';
    }
    var formCheck = document.getElementById("form1");
    if(formCheck){
        document.getElementById('EU').style.display = 'block';
    }
</script>